INSERT INTO `papers` VALUES ('2547', ' n. rudiment ~  Adj. simple; not complex ~  Syn. basic\n اوليه، بدوى، ابتدايى\n\n-he has a rudimentary knowledge of computers.\n-The restaurants superiority was established shortly after it opened.');
