INSERT INTO `papers` VALUES ('2549', ' Adv. superficially ~  Adj. simple; not deep; near the surface ~  Syn. shallow\n سطحى، سرسرى، ظاهرى\n\n-The inspector determined that The crack In The bridge was Only superficial\n');
INSERT INTO `papers` VALUES ('2550', 'V. terrify ~  Adv. terrifyingly ~  Adj. filled with fear ~  Syn. frightening\n وحشت زده کردن\n\n-to be In a violent storm is a terrifying experience.\n');
