INSERT INTO `papers` VALUES ('2579', 'V. report ~  Adv. to know by report; unconfirmed; supposedlyn. report ~  Adj. reported ~  Syn. Rumored\n شايعه، شايعه گفتن و يا پخش کردن\n\n-The students reportedly sent a representative, but She has not arrived yet.\n');
INSERT INTO `papers` VALUES ('2580', 'V. shift n. change in position or direction ~  Adj. shifting, shifty ~  Syn. Switch\n کليد مبدل، تغيير دادن، شيفت کار، جابجائى، تغيير جهت ، تعويض کردن، تغيير مکان دادن، انتقال دادن، نوبت کار\n\n-The shift In The wind was helpful to The sailors.\n');
INSERT INTO `papers` VALUES ('2581', 'V. to experience difficulty; to worsen in quality; to experience pain n. suffering, sufferer ~  Adj. suffering ~  Syn. endure\n اجازه دادن، زحمت، زيان ديدن، تحمل کردن، تن در دادن به، رنج بردن\n\n-The old man suffers From loss of memory.\n');
INSERT INTO `papers` VALUES ('2582', ' Adv. crucially ~  Adj. of great importance; extremely necessary ~  Syn. critical\n وخيم، بسيار سخت، قاطع\n\n-Favorable weather is crucial to a Good harvest.\n');
INSERT INTO `papers` VALUES ('2583', ' n. a very important or famous person, usually associated with a high position in government  ~  Syn. notable\n شخص بزرگ، عالى مقام\n\n-Every dignitary In Washington was invited to The wedding.\n');
INSERT INTO `papers` VALUES ('2584', 'V. to escape in a tricky way n. elusiveness ~  Adj. elusive ~  Syn. evade\n اجتناب کردن از، طفره زدن، دورى کردن از\n\n-The criminal has eluded The police for months.\n');
INSERT INTO `papers` VALUES ('2585', ' Adv. evidentlyn. evidence ~  Adj. easy to see, usually because of some proof ~  Syn. apparent\n بديهى، آشکار، مشهود\n\n-It is evident that you are not feeling.\n');
INSERT INTO `papers` VALUES ('2586', 'V. to use completely; to expend all energy; very thorough ~  Adv. exhaustivelyn. exhaustion ~  Adj. exhaustive, exhausting, exhausted ~  Syn. deplete\n تخليه کردن، لوله اگزوز، خروج گاز يا بخار، تمام شدن انرژى، تهى کردن، خسته کردن، ازپاى در آوردن، تمام کردن');
INSERT INTO `papers` VALUES ('2587', 'V. extend ~  Adv. extensivelyn. extension ~  Adj. large in area or number to offer; to make longer.&nbsp;&nbsp;an extending; an additional part ~  Syn. comprehensive\n شديد، زياد، پهن، عريض، گسترده، پهناور، وسيع، بزرگ\n\n-The extensive snowfall caused proble');
INSERT INTO `papers` VALUES ('2588', ' Adv. very; to the very end, the highest extentn. extreme, extremist ~  Adj. extreme, extremist ~  Syn. highly\n بشدت، به افراط\n\n-When The concert was canceled, Some customers became extremely upset.\n');
INSERT INTO `papers` VALUES ('2589', 'V. to be in the presence of an oppose   ~  Syn. confront\n پيشانى، رخ، رخسار، صورت، نما، مواجه شدن، چهره، طرف، سمت، وجه ،ظاهر، منظر، روبروايستادن، مواجه شدن، روياروى شدن\n\n-The mountain climbers faced grave danger on The cliff.\n');
INSERT INTO `papers` VALUES ('2590', ' n. element or component ~  Adj. faceted ~  Syn. aspect\n صورت کوچک، تراش، شکل، منظر، بند، مفصل\n\n-The proposal had Many beneficial facets\n');
INSERT INTO `papers` VALUES ('2591', ' Adv. heroicallyn. heroine (female), heroics, a person remembered for an act of goodness or bravery ~  Adj. heroic ~  Syn. idol\n قهرمان، دلاور، پهلوان داستان\n\n-he is a hero In The eyes of His admirers.\n');
INSERT INTO `papers` VALUES ('2592', ' Adv. inaccessiblyn. inaccessibility ~  Adj. something that cannot be reached or communicated with ~  Syn. remote\n ارتباط ناپذير، خارج از دسترس\n\n-The summit of The mountain was inaccessible\n');
INSERT INTO `papers` VALUES ('2593', ' Adv. in a clear, easy to understand way ~  Adj. obvious ~  Syn. evidently\n بطورآشکار يا معلوم، بديهى است که\n\n-It had obviously rained.\n');
INSERT INTO `papers` VALUES ('2594', 'V. predict ~  Adv. in a way that foretells future eventsn. prediction ~  Adj. predictable ~  Syn. expectedly\n پيشگويى کردن، پيش بينى کردن\n\n-She predictably forgot to do Her assignment.\n');
INSERT INTO `papers` VALUES ('2595', 'V. to find the answer n. solution  ~  Syn. resolve\n حل کردن، رفع کردن، گشادن، باز کردن\n\n-They solved The problem In a way that benefited The entire neighborhood.\n');
INSERT INTO `papers` VALUES ('2596', 'V. suit ~  Adv. suitably ~  Adj. appropriate, correct; convenient ~  Syn. appropriate\n درخورد، مناسب، شايسته، فراخور\n\n-Her dress was not suitable for The occasion.\n');
INSERT INTO `papers` VALUES ('2597', ' Adv. amply ~  Adj. more than enough ~  Syn. sufficient\n فراخ، پهناور، وسيع، فراوان، مفصل، پر، بيش از اندازه\n\n-There is ample evidence that The young man was speeding When The accident occurred.\n');
INSERT INTO `papers` VALUES ('2598', '  ~  Adj. having little rain or water ~  Syn. dry\n کم آب، خشک، باير، لم يزرع، خالى، بيمزه، بيروح، بى لطافت\n\n-The area known as The Sahara Desert is one of The most arid place In The world.\n');
INSERT INTO `papers` VALUES ('2599', 'V. to miss or keep away from n. avoidance ~  Adj. avoidable ~  Syn. avert\n دورى کردن از، احتراز کردن، اجتناب کردن، طفره رفتن از، موقوف کردن\n\n-She could not avoid letting Her feelings show.\n');
INSERT INTO `papers` VALUES ('2600', 'V. to show little fear or regard for rules or established norms; to challenge ~  Adv. defyingly ~  Adj. defying ~  Syn. resist\n به مبارزه طلبيدن، تحريک جنگ کردن\n\n-I defy you to find that book in the librarys collection.\n');
INSERT INTO `papers` VALUES ('2601', 'V. to pass a law n. enactment ~  Adj. enacted ~  Syn. Legislate\n بصورت قانون درآوردن، وضع کردن(قانون )، تصويب کردن، نمايش دادن\n\n-Congress enacted The legislation during its last session.\n');
INSERT INTO `papers` VALUES ('2602', ' Adv. evenlyn. evenness ~  Adj. regular, smooth; in equal parts ~  Syn. Equitable\n مسطح، تراز، مساوى، هموارکردن، صاف کردن، واريز کردن، اعداد جفت، عدد زوج\n\n-The sound isnt even turn up the left speaker.\n');
INSERT INTO `papers` VALUES ('2603', 'V. to pretend, make believe  ~  Adj. feigned ~  Syn. simulate\n وانمود کردن، بخود بستن، جعل کردن\n\n-She feigned illness When It was Time to visit The dentist.\n');
INSERT INTO `papers` VALUES ('2604', 'V. fertilize n. fertility, fertilizer ~  Adj. able to produce abundantly ~  Syn. Rich\n حاصلخيز، پرثمر، بارور، پربرکت\n\n-The delta areas of rivers are known for Their fertile soil.\n');
INSERT INTO `papers` VALUES ('2605', 'V. freshen ~  Adv. caught or produced not long agon. freshness ~  Adj. fresh ~  Syn. recently\n به تازگى، از روى بى تجربگى، با نفس يا نيروى تازه\n\n-Freshly harvested produce is hard to find In The winter months.\n');
INSERT INTO `papers` VALUES ('2606', 'V. function ~  Adv. functionallyn. the normal purpose of something ~  Adj. functional ~  Syn. Role\n طرزکار، کارايى، فعاليت، تابع، پيشه، مقام، ماموريت، عمل کردن، کارکردن، وظيفه داشتن، آيين رسمى\n\n-It is The function of The director to organize and lead The ');
INSERT INTO `papers` VALUES ('2607', ' Adv. fundamentally ~  Adj. a primary or basic element ~  Syn. essential\n ابتدايى، پايه اى، اصولى، اساسى، اوليه، اساس، اصلى، بنيادى\n\n-The student government promised fundamental changes In The registration process.\n');
INSERT INTO `papers` VALUES ('2608', ' Adv. indiscriminately ~  Adj. not chosen carefully; unplanned ~  Syn. arbitrary\n يکسره، ناشى از عدم تبعيض، خالى از تبعيض\n\n-The indiscriminate arrangement of The products made The store confusing.\n');
INSERT INTO `papers` VALUES ('2609', 'V. select ~  Adv. selectivelyn. selection, selectivity ~  Adj. select, carefully chosen ~  Syn. discriminating\n برگزيننده، انتخابى، گزينشى، انتخاب کننده، داراى حسن انتخاب\n\n-They were very selective When They chose The members of The academic team.\n');
INSERT INTO `papers` VALUES ('2610', ' n. space, spaciousness ~  Adj. spaciously, having a lot of room ~  Syn. expansive\n فراخ، جادار، وسيع، جامع، گشاد، مفصل\n\n-The spacious plains of the Midwest make up the nations breadbasket.\n');
INSERT INTO `papers` VALUES ('2611', 'V. to fight without surrender; to persist   ~  Syn. survive\n تاب آوردن، مقاومت کردن با، ايستادگى کردن در برابر، تحمل کردن، مخالفت کردن، استقامت ورزيدن\n\n-She cannot withstand The pressures of Her job.\n');
INSERT INTO `papers` VALUES ('2612', ' n. durability, duration ~  Adj. something that lasts a long time&nbsp;&nbsp;time during which something lasts ~  Syn. sturdy\n پايدار، باودام، پايا، ديرپاى\n\n-It was a durable refrigerator, but It finally broke.\n');
INSERT INTO `papers` VALUES ('2613', 'V. to prefer&nbsp;&nbsp;advantageous ~  Adv. favorablyn. favor ~  Adj. favorable, favorite ~  Syn. support\n توجه، مساعدت، مرحمت کردن، نيکى کردن به، طرفدارى کردن\n\n-The supervisor favored The first of The two plans.\n');
INSERT INTO `papers` VALUES ('2614', ' n. gain, to obtain something needed or useful; to increase the amount of something ~  Adj. gainful ~  Syn. attain\n منفعت، نفع، بهره، تقويت، حصول، صرفه، استفاده، افزايش، سود بردن، فايده بردن، پيدا کردن، کسب کردن، باز يافتن، بهبودى يافتن، رسيدن، زياد شدن\n\n');
INSERT INTO `papers` VALUES ('2615', 'V. to produce n. generation, generator ~  Adj. generated ~  Syn. create\n گسترش يافتن، آفريدن، ساختن، زاييدن، توليد کردن، احداث کردن، به وجود آوردن، حاصل کردن، توليد نيرو کردن\n\n-The appearance of The dance troupe generated a lot of excitement.\n');
INSERT INTO `papers` VALUES ('2616', 'V. to stop or discontinue ~  Adv. haltinglyn. halt ~  Adj. halting ~  Syn. stop\n متوقف کردن، متوقف، درنگ، سکته، ايست کردن، مکث کردن، لنگيدن\n\n-Bus service to The City was halted due to poor road conditions.\n');
INSERT INTO `papers` VALUES ('2617', 'V. to deal with or control n. handle  ~  Syn. manage\n با دست عمل کردن، نگهدارنده، دستگيره جابجا کردن، وسيله، لمس، دست زدن به، بکار بردن، سرو کارداشتن با، رفتار کردن، استعمال کردن\n\n-They handled themselves very well given The circumstances.\n');
INSERT INTO `papers` VALUES ('2618', 'V. to give protection; not to express a desire or opinion, usually bad   ~  Syn. shelter\n لنگرگاه، بندرگاه، پناهگاه، پناه دادن، پناه بردن، لنگر انداختن، پروردن\n\n-he harbors ill feelings for her.\n');
INSERT INTO `papers` VALUES ('2619', 'V. hair ~  Adv. harmfullyn. harm ~  Adj. something that causes pain or damage ~  Syn. unhealthy\n مضر، پرگزند\n\n-Excessive radiation is harmful to The body.\n');
INSERT INTO `papers` VALUES ('2620', ' Adv. insignificantlyn. insignificance ~  Adj. not important; of little value ~  Syn. Meaningless\n ناچيز\n\n-The amount of rainfall This summer has been insignificantly\n');
INSERT INTO `papers` VALUES ('2621', ' Adv. mysteriouslyn. mystery, mysteriousness ~  Adj. not easily understood or figured out ~  Syn. baffling\n اسرار آميز، مرموز، مبهم\n\n-he had a mysterious effect on Everyone who heard him speak.\n');
INSERT INTO `papers` VALUES ('2622', ' Adv. perilouslyn. peril ~  Adj. threatening or risky; harmful ~  Syn. Dangerous\n مخاطره آميز، خطرناک\n\n-It is perilous to exceed The speed limit.\n');
INSERT INTO `papers` VALUES ('2623', 'V. reschedule at a later time; put off n. postponement  ~  Syn. delay\n به تاخير انداختن، بتعويق افتادن، عقب انداختن، پست تر دانستن، در درجه دوم گذاشتن\n\n-The teacher postponed The lab experiment.\n');
INSERT INTO `papers` VALUES ('2624', 'V. to encourage or advertise; to elevate in rank or grade n. promoter, promotion  ~  Syn. boost\n تأسيس کردن، توسعه دادن، بالا بردن، ترفيع دادن، ترويج کردن\n\n-Many nations promote tourism to lure foreign currency.\n');
INSERT INTO `papers` VALUES ('2625', 'V. to refuse n. rejection  ~  Syn. refuse\n پس زدن، عقب زدن دشمن، رد کردن، نپذيرفتن\n\n-The insurance company rejected The claim.\n');
INSERT INTO `papers` VALUES ('2626', ' Adv. substantial ~  Adj. substantive, important; strongly made; of value ~  Syn. significant\n ذاتى، جسمى، اساسى، مهم، محکم، قابل توجه\n\n-The discovery of a vaccine for smallpox was a substantial medical achievement.\n');
INSERT INTO `papers` VALUES ('2627', ' Adv. conscientiously ~  Adj. showing serious purpose; one who works carefully and with enthusiasm ~  Syn. meticulous\n باوجدان، وظيفه شناس\n\n-She is a conscientious representative of The student body.\n');
INSERT INTO `papers` VALUES ('2628', 'V. to make something known to others; to communicate   ~  Syn. communicate\n هدايت کردن، رساندن، بردن، حمل کردن\n\n-The manager of The store conveyed His displeasure directly to The workers.\n');
INSERT INTO `papers` VALUES ('2629', 'V. to surround completely; to envelop   ~  Syn. include\n احاطه کردن، حلقه زدن، دارا بودن، شامل بودن، دربرگرفتن، محاصره کردن\n\n-Her plan of The study encompasses Every aspect of computer science.\n');
INSERT INTO `papers` VALUES ('2630', 'V. expand n. expansion, the act of making larger ~  Adj. expandable ~  Syn. growth\n وسعت، افزايش، گسترش، توسعه، انبساط\n\n-Expansion occurs When matter is heated.\n');
INSERT INTO `papers` VALUES ('2631', 'V. to cause to become greater n. height ~  Adj. heightened ~  Syn. intensify\n بلند کردن، بلندتر کردن، بالا بردن، زياد کردن\n\n-A very successful interview can heighten a candidates chances to get a job.\n');
INSERT INTO `papers` VALUES ('2632', 'V. to emphasize the part of a greater whole n. highlight  ~  Syn. emphasize\n مشخص، نشان شده، پررنگ\n\n-The manual highlights basic operation of The videotape player.\n');
INSERT INTO `papers` VALUES ('2633', ' Adv. by accident; without paying attention; unexpectedly ~  Adj. inadvertent ~  Syn. carelessly\n از روى بى احتياطى، سهواً\n\n-The reporters had inadvertently failed to include The name of one of The dignitaries.\n');
INSERT INTO `papers` VALUES ('2634', ' Adv. inevitablyn. inevitability ~  Adj. something that cannot be prevented from happening ~  Syn. unavoidable\n نا چار، ناگزير، اجتناب ناپذير، حتمى، بديهى\n\n-When two weather systems meet, unsettled weather conditions are inevitable\n');
INSERT INTO `papers` VALUES ('2635', ' n. infant, in the beginning stages of development ~  Adj. infantile ~  Syn. beginning\n طفوليت، نوباوگى، کودکى، خردى، ابتدا، شروع\n\n-The new theory is In its infancy and Will be thoroughly tested by its critics.\n');
INSERT INTO `papers` VALUES ('2636', ' Adv. caused by something that cannot be explained by the laws of nature.n. miracle ~  Adj. miraculous ~  Syn. astonishingly\n بطور حيرت انگيز، معجزآسا\n\n-Miraculously, he was unharmed After being hit by lightning.\n');
INSERT INTO `papers` VALUES ('2637', 'V. to find and bring back n. retrieval ~  Adj. retrieve ~  Syn. recover\n بازيافتن، دوباره بدست آوردن، پس گرفتن، جبران کردن\n\n-Will Detroit retrieve its status as The Car manufacturing center of The world?\n');
INSERT INTO `papers` VALUES ('2638', ' Adv. done according to a plann. system ~  Adj. systematic ~  Syn. methodically\n با روش معين، مرتباً، از روى يک اسلوب معين\n\n-The plan was developed systematically by a team of experts.\n');
INSERT INTO `papers` VALUES ('2639', '  ~  Adj. not probable ~  Syn. doubtful\n غير جذاب، قابل اعتراض، بعيد\n\n-rain is unlikely during The summer.\n');
INSERT INTO `papers` VALUES ('2640', '  ~  Adj. without good reason or cause; inappropriate ~  Syn. unjustified\n غيرقابل ضمانت، توجيه نکردنى، بيجا\n\n-His negative reaction was unwarranted\n');
INSERT INTO `papers` VALUES ('2641', ' n. the highest point  ~  Syn. apex\n سمت الرأس، نقطه قائم برناظر، اوج محور قائم بر افق نقاط، بالاترين نقطه آسمان، قلّه، اوج\n\n-he reached The zenith of His profession at a very young age.\n');
INSERT INTO `papers` VALUES ('2642', 'V. to shake or move; to cause worry n. agitation, agitator  ~  Syn. disturb\n بهم زدن، بکارانداختن، تحريک کردن، تکان دادن، آشفتن، پريشان کردن، سراسيمه کردن\n\n-The fact that She had not arrived by midnight agitated Her parents.\n');
INSERT INTO `papers` VALUES ('2643', 'V. confide ~  Adv. confidentiallyn. confidant ~  Adj. confidential, to be said or written in secret ~  Syn. secret\n خيلى محرمانه، داراى مأموريت محرمانه، راز دار\n\n-We were told that The information is strictly confidential\n');
INSERT INTO `papers` VALUES ('2644', ' Adv. delightfullyn. delight ~  Adj. delightful, to be satisfied; very happy ~  Syn. elated\n محظوظ\n\n-he was delighted With The result of The experiment.\n');
INSERT INTO `papers` VALUES ('2645', ' Adv. in a careful, polite mannern. discretion ~  Adj. discreet ~  Syn. cautiously\n محتاطانه، از روى بصيرت\n\n-The teacher discreetly told The parents about Her problems.\n');
INSERT INTO `papers` VALUES ('2646', 'V. document n. documentation ~  Adj. documentary, proven with written evidence ~  Syn. proven\n مدرک، دستاويز، ملاک، سنديّت دادن\n\n-he had documented proof that The bank had made an error.\n');
INSERT INTO `papers` VALUES ('2647', ' Adv. slowly but surely ~  Adj. gradual ~  Syn. steadily\n بتدريج، رفته رفته\n\n-The bay has gradually deteriorated over The years.\n');
INSERT INTO `papers` VALUES ('2648', 'V. to have an effect on a persons point of view or behavior; to change the course of events n. influence ~  Adj. influential ~  Syn. affect\n اعتبار، برترى، توانايى، تجلّى، نفوذ کردن بر، تحت نفوذ خود قرار دادن، تأثير کردن بر، وادار کردن، ترغيب کردن\n\n-He w');
INSERT INTO `papers` VALUES ('2649', ' Adv. inordinately ~  Adj. a large amount or quality; more than reasonable ~  Syn. excessive\n بى اندازه، بيش از حد، مفرط، غير معتدل\n\n-The airlines had to cancel an inordinate number of flights due to The fog.\n');
INSERT INTO `papers` VALUES ('2650', ' Adv. happening immediately; in a short period of timen. instant ~  Adj. instantaneous, instant ~  Syn. immediately\n فوراً، بيدرنگ، به محض اينکه\n\n-The computer finished The job instantly\n');
INSERT INTO `papers` VALUES ('2651', ' Adv. with definite purpose and planningn. intention, intent ~  Adj. intentional ~  Syn. deliberately\n دانسته، عمداً، قصدا\n\n-The machine was left on intentionally\n');
INSERT INTO `papers` VALUES ('2652', ' Adv. intrinsically ~  Adj. belonging to the essential nature of something ~  Syn. inherent\n بي واسطه، ذاتى، اصلى، باطنى، طبيعى، ذهنى، روحى، حقيقى، مرتب، شايسته\n\n-a penny has little intrinsic value.\n');
INSERT INTO `papers` VALUES ('2653', 'V. to flood n. inundation  ~  Syn. overwhelm\n از آب پوشانيدن، زير سيل پوشاندن، اشباع کردن\n\n-The radio stations were inundated With reports of a severe traffic accident.\n');
INSERT INTO `papers` VALUES ('2654', 'V. to become concerned with or connected to n. involvement ~  Adj. involved ~  Syn. include\n گرفتار کردن، گير انداختن، وارد کردن، گرفتارشدن، در گير کردن يا شدن\n\n-She involved herself In Many activities to meet new friends.\n');
INSERT INTO `papers` VALUES ('2655', ' Adv. nominally ~  Adj. very small; in form but not in substance ~  Syn. moderate\n غير واقعى، اسمى، صورى، جزئى، کم قيمت\n\n-The office building was sold at a nominal price.\n');
INSERT INTO `papers` VALUES ('2656', 'V. presume ~  Adv. reported but not confirmedn. presumption ~  Adj. presumable ~  Syn. supposedly\n احتمالاً\n\n-The old wreck was presumably located to The southwest of Florida.\n');
INSERT INTO `papers` VALUES ('2657', ' Adv. absurdlyn. absurdity ~  Adj. clearly false, without reason ~  Syn. ridiculous\n پوچ، ناپسند، ياوه، مزخرف، بى معنى، نامعقول، عبث، مضحک\n\n-Confidentially, I think His suggestion is absurd\n');
INSERT INTO `papers` VALUES ('2658', 'V. abuse ~  Adv. abusivelyn. the act of using or treating things in an incorrect way ~  Adj. abusive ~  Syn. misuse\n سوء مصرف، بد بکار بردن، سوء استفاده کردن از، ضايع کردن، تجاوز به حقوق کسى کردن، ننگين کردن\n\n-The constant abuse of The environment Will ha');
INSERT INTO `papers` VALUES ('2659', 'V. allocate n. a share; a part set aside for a special purpose; an assignment of portions ~  Adj. allocated ~  Syn. distribution\n واگذار کردن، سهميه دادن، تخحيص\n\n-His allocation of materials was gradually used up.\n');
INSERT INTO `papers` VALUES ('2660', 'V. balance n. balance ~  Adj. a state where everything is of the same size or weight; an element on one side that counters an equal element on the other ~  Syn. equalized\n متعادل، متوازن\n\n-he made a balanced presentation of both points of view.\n');
INSERT INTO `papers` VALUES ('2661', 'V. conserve n. conservative, conservationist, conservatism, the act of using carefully; setting aside for future use favoring traditional values  ~  Syn. preservation\n نگهدارى، حفاظت، حفظ منابع طبيعى\n\n-Conservation of forest land is The primary objective ');
INSERT INTO `papers` VALUES ('2662', ' Adv. fallaciouslyn. fallacy ~  Adj. having errors ~  Syn. incorrect\n غلط، سفسطه آميز، مغالطه آميز\n\n-Her fallacious argument could not be defended.\n');
INSERT INTO `papers` VALUES ('2663', ' Adv. feasiblyn. feasibility ~  Adj. able to be done ~  Syn. possible\n شدنى، عملى، امکان پذير، ميسر، ممکن، محتمل\n\n-It is a feasible design for The high-rise building.\n');
INSERT INTO `papers` VALUES ('2664', 'V. lack n. a need for; an insufficient amount  ~  Syn. shortage\n کمبود، نبودن، نداشتن، احتياج، فقدان، کسرى، فاقد بودن، ناقص بودن، کم داشتن\n\n-There was an inordinate lack of rain last fall.\n');
INSERT INTO `papers` VALUES ('2665', '  ~  Adj. to be stretched, easily shaped ~  Syn. flexible\n سربزير، مطيع، تاشو، نرم، خم کردن، تاکردن، تمرين نرمش کردن\n\n-The dancer has a limber body.\n');
INSERT INTO `papers` VALUES ('2666', ' n. ways  ~  Syn. methods\n دارايى، استطاعت، وسايل، توانایى، درآمد\n\n-he was told to finish The joy by any means available to him.\n');
INSERT INTO `papers` VALUES ('2667', ' Adv. narrowlyn. narrowness ~  Adj. small from one side to the other; limited ~  Syn. thin\n تنگ،دراز و باريک، کم پهنا، باريک کردن، محدود کردن، کوته فکر\n\n-The canal was extremely narrow\n');
INSERT INTO `papers` VALUES ('2668', 'V. preconceive n. an opinion formed in advance without experience or knowledge of something ~  Adj. preconceived ~  Syn. bias\n حضور پيش از وقت، تصديق بلا تصور، تعصب\n\n-It is difficult to overcome preconceptions If We are not open to new ideas.\n');
INSERT INTO `papers` VALUES ('2669', ' Adv. robustlyn. robustness ~  Adj. showing good health; in good shape ~  Syn. energetic\n قوى هيکل، تنومند، ستبر، هيکل دار\n\n-The robust economy is expected to continue growing quickly.\n');
INSERT INTO `papers` VALUES ('2670', 'V. steady ~  Adv. steadilyn. steadiness ~  Adj. firm; in a fixed position; without change; reliable, dependable ~  Syn. constant\n پابرجا، مسير ثابت، روى مسير، پرپشت، ثابت، پى درپى، مداوم، پيوسته ويکنواخت کردن، استوار يا محکم کردن\n\n-Steady growth is projec');
INSERT INTO `papers` VALUES ('2671', ' Adv. swiftlyn. swiftness ~  Adj. quick ~  Syn. fast\n سريع، چابک، تندرو، فرز، باسرعت\n\n-The contestants were swift thinkers.\n');
INSERT INTO `papers` VALUES ('2672', ' n. antique ~  Adj. too old to be presently useful; outmoded ~  Syn. old-fashioned\n کهنه، منسوخ، متروک، قديمى\n\n-This antiquated machinery breaks down too frequently.\n');
INSERT INTO `papers` VALUES ('2673', 'V. cohere ~  Adv. coherentlyn. coherence, cohesion ~  Adj. cohesive, well reasoned; ideas that are clearly presented sticking together as a group ~  Syn. logical\n مربوط، داراى ارتباط يا نتيجه منطقى، منسجم\n\n-It was a well-balanced, coherent presentation.\n');
INSERT INTO `papers` VALUES ('2674', 'V. to grow; to increase; to become more complete n. development, developer ~  Adj. developing ~  Syn. evolve\n گسترش دادن، گسترش وضعيت، آشکار کردن، توسعه دادن، بسط دادن، پرورش دادن، ايجاد کردن\n\n-The management team developed The idea over a period of years');
INSERT INTO `papers` VALUES ('2675', 'V. to make up, usually with an intent to fool or trick; to lie n. fabrication ~  Adj. fabricated ~  Syn. invent\n سوار کردن، ساختن، بافتن، از کار درآوردن، تقليد کردن، جعل کردن\n\n-The executive fabricated The story about The merger.\n');
INSERT INTO `papers` VALUES ('2676', 'V. investigate n. investigator, a careful examination in order to determine facts ~  Adj. investigative ~  Syn. probe\n تجسس، جستجو، بازجويى، بررسى، تحقيق، رسيدگى\n\n-The comprehensive investigation of The bank revealed No illegal activity.\n');
INSERT INTO `papers` VALUES ('2677', 'V. normalize ~  Adv. commonly, usuallyn. normalization, norm ~  Adj. normal ~  Syn. typically\n معمولاً، به طور عادى\n\n-It is normally quite cold This Time of The year.\n');
INSERT INTO `papers` VALUES ('2678', 'V. to sense; to be aware ~  Adv. noticeablyn. notice ~  Adj. noticeable ~  Syn. observe\n تذکر، آگاهى، اعلاميه، خبر، توجه، اطلاع، اخطار، ملتفت شدن، ديدن، شناختن، ملاحضه کردن\n\n-The doctor noticed a small fracture in the patients finger.\n');
INSERT INTO `papers` VALUES ('2679', ' n. an idea, belief, or opinion  ~  Syn. concept\n تصور، انديشه، نظريه، خيال، ادراک، فکرى، مفهوم\n\n-She has The notion that She wants to become an architect.\n');
INSERT INTO `papers` VALUES ('2680', '  ~  Adj. something unusual, uncommon; new ~  Syn. original\n نو، جديد، بديع، رمان، کتاب داستان\n\n-The physicist had Some novel ideas about traveling at The speed of light.\n');
INSERT INTO `papers` VALUES ('2681', 'V. oppose n. the state of acting against; not being in agreement ~  Adj. oppositional, oppositionary ~  Syn. resistance\n تناقض، تقابل، اقليت، دشمنى، ضديت، مخالفت، مقاومت، تضاد، مقابله\n\n-The students voiced Their opposition to The rise In tuition.\n');
INSERT INTO `papers` VALUES ('2682', 'V. to make a written or oral notation; to copy n. record, recording, recorder ~  Adj. recorded ~  Syn. register\n نگاشتن، ضبط کردن، ضبط شدن، دفتر، مدرک کتبى \n\n-The coldest temperatures In The United States have been recorded at International Falls, Minneso');
INSERT INTO `papers` VALUES ('2683', 'V. to tell; to show a connection between two things n. relation, relationship ~  Adj. related ~  Syn. communicate\n باز گو کردن، گزارش دادن، شرح دادن، نقل کردن\n\n-Although They Did not agree With The plan, They Did not relate Their opposition to it.\n');
INSERT INTO `papers` VALUES ('2684', 'V. to think that something is true, but having no proof n. suspicion, suspect ~  Adj. suspected ~  Syn. speculate\n بدگمان شدن از، گمان کردن، شک داشتن، مظنون، موردشک\n\n-he suspected that The substance was not present In The compound.\n');
INSERT INTO `papers` VALUES ('2685', '  ~  Adj. with no preconceptions ~  Syn. objective\n بيغرض، عارى از تعصب، بدون تبعيض، تحت تأثير واقع نشده\n\n-Her unbiased Analysis of The problem allowed Her to find The solution more rapidly.\n');
INSERT INTO `papers` VALUES ('2686', 'V. vary ~  Adv. variablyn. variant, variety, variation, variability ~  Adj. variable, various, being of many different types ~  Syn. diverse\n رنگارنگ، گوناگون، متنوع\n\n-The class expressed varied opinions about The movie.\n');
INSERT INTO `papers` VALUES ('2687', 'V. to highlight; to give more importance to n. accentuation ~  Adj. accentuated ~  Syn. emphasize\n با تکيه تلفظ کردن، تکيه دادن، تأکيد کردن، اهميت دادن، برجسته نمودن\n\n-The colorful dress accentuated The joy of The occasion.\n');
INSERT INTO `papers` VALUES ('2688', 'V. to hide the usual appearance of something n. disguise ~  Adj. disguised ~  Syn. conceal\n استتار، پوشش، تغيير قيافه دادن، جامه مبدل پوشيدن، نهان داشتن، پنهان کردن، لباس مبدل، تغيير قيافه\n\n-It is hard to disguise The fact that business is slow.\n');
INSERT INTO `papers` VALUES ('2689', 'V. financed, to provide money ~  Adv. financiallyn. finance, finances ~  Adj. financial ~  Syn. fund\n قسمت مالى يا دارايى، پرداخت هزينه، علم دارايى، تهيه پول کردن، سرمايه تهيه کردن، سرمايه گذارى\n\n-The art exhibition was financed by a private foundation.\n');
INSERT INTO `papers` VALUES ('2690', 'V. to begin; to establish; to take decisive action without help n. initiation, initiative ~  Adj. initiated ~  Syn. launch\n ابداع کردن، ابتکار کردن، تازه وارد کردن، بنياد نهادن، نخستين قدم را برداشتن\n\n-The newcomer initiated The long citizenship process.\n');
INSERT INTO `papers` VALUES ('2691', ' n. innovator, innovation ~  Adj. something newly introduced; creative ~  Syn. creative\n نو\n\n-This innovative project is worthy of support.\n');
INSERT INTO `papers` VALUES ('2692', 'V. to tell a story; relate n. narrative, narration, narrator ~  Adj. narrative ~  Syn. relate\n داستانى را تعريف کردن، داستان سرايى کردن، نقالى کردن، شرح دادن\n\n-Walter Cronkite narrated The documentary film.\n');
INSERT INTO `papers` VALUES ('2693', '  Conj. in spite of that ~  Syn. nonetheless\n با اين حال، با اين وجود، عليرغم، هنوز، باز\n\n-She was quite sick; nevertheless, she attended all of her classes.\n');
INSERT INTO `papers` VALUES ('2694', ' Adv. now and then; once in a whilen. occasion ~  Adj. occasional ~  Syn. sometimes\n گهگاه، گاه و بيگاه، بعضى از اوقات\n\n-Extreme heat occasionally causes health problems.\n');
INSERT INTO `papers` VALUES ('2695', 'V. to leave out, not include n. omission ~  Adj. omitted ~  Syn. neglect\n حذف کردن، از قلم انداختن\n\n-he inadvertently omitted Some important data From The report.\n');
INSERT INTO `papers` VALUES ('2696', ' Adv. outlandishly ~  Adj. strange and unpleasant; beyond accepted norms ~  Syn. bizarre\n بيگانه وار، عجيب وغريب\n\n-His outlandish dens demonstrated His creativity.\n');
INSERT INTO `papers` VALUES ('2697', 'V. to defeat, fight with success; to take control of an individual   ~  Syn. conquer\n غلبه کردن، چيره شدن، پيروز شدن بر، مغلوب ساختن، غلبه يافتن\n\n-The young woman was overcome With emotion When She learned She had won a scholarship.\n');
INSERT INTO `papers` VALUES ('2698', '-The clerk was Only partially responsible for The error.\n اندکى، تا يک اندازه \n\n-The business venture was Only a partial success.\n');
INSERT INTO `papers` VALUES ('2699', 'V. to accept formally by vote n. passage ~  Adj. passable ~  Syn. approve\n صادر شدن، اجازه عبور، گذشتن، عبور کردن، تصويب کردن، قبول شدن، پاس دادن، رايج شدن، اجتناب کردن، جواز، گذرنامه، بليط\n\n-The proposed amendment passed unanimously.\n');
INSERT INTO `papers` VALUES ('2700', 'V. to represent; to act n. portrayal  ~  Syn. depict\n تصوير کشيدن، توصيف کردن، مجسم کردن\n\n-The girl portrayed an orphan.\n');
INSERT INTO `papers` VALUES ('2701', 'V. to turn in, offer for evaluation n. submission  ~  Syn. propose\n تسليم کردن، تسليم شدن، تقديم داشتن، ارائه دادن، پيشنهادکردن، گردن نهادن، مطيع شدن\n\n-The architects had to submit plans reflecting The new specifications.\n');
INSERT INTO `papers` VALUES ('2702', 'V. to move from good to bad, or from much to little, to refuse n. decline  ~  Syn. decrease\n کاهش، شيب پيدا کردن، رد کردن، نپذيرفتن، خم شدن، مايل شدن، تنزل کردن، کاستن\n\n-The old mans health has declined since he retired.\n');
INSERT INTO `papers` VALUES ('2703', 'V. to collect n. gathering  ~  Syn. collect\n گرد آمدن، جمع شدن، بزرگ شدن، جمع کردن، گرد کردن، گردآورى کردن، نتيجه گرفتن، استباط کردن\n\n-he carefully gathered His thoughts just before The interview.\n');
INSERT INTO `papers` VALUES ('2704', 'V. motion n. the state of changing ones position to direct by moving   ~  Syn. movement\n جنبش، تکان، حرکت، جنب وجوش، پيشنهاد کردن، طرح دادن، اشاره کردن\n\n-The motion of The flame was hypnotic.\n');
INSERT INTO `papers` VALUES ('2705', '  ~  Adj. strongly supporting a group or point of view ~  Syn. biased\n طرفدار، حامى، پيرو متعصب \n\n-Partisan political infighting caused Congresss influence to decline.\n');
INSERT INTO `papers` VALUES ('2706', 'V. pattern n. a regular, repeated arrangement or action ~  Adj. patterned ~  Syn. habit\n بطورنمونه ساختن، شکل، الگو قالب، نقش، صفات فردى، خصوصيات، نظيربودن، همتا بودن، تقليد کردن، نقشه ساختن، به عنوان الگو بکاربردن\n\n-The bright pattern of The monarch butt');
INSERT INTO `papers` VALUES ('2707', ' Adv. phenomenallyn. phenomenon, natural events or facts; strange or notable happenings ~  Adj. phenomenal ~  Syn. events\n پديده ها، حادثه، واقعه، رويداد، پيشامد\n\n-rain showers are almost unknown phenomena In The Atacama Desert of Chile.\n');
INSERT INTO `papers` VALUES ('2708', ' n. philanthropist, philanthropy ~  Adj. a feeling of love for people, usually resulting in financial aid to worthy causes ~  Syn. humanitarian\n نوع پرست، بشردوست\n\n-The philanthropic work of The foundation benefits all sectors of society.\n');
INSERT INTO `papers` VALUES ('2709', ' Adv. placidly ~  Adj. quiet, not easily upset ~  Syn. calm\n آرام، راحت، متين\n\n-The placid nature of Her personality made Her easy to work with.\n');
INSERT INTO `papers` VALUES ('2710', ' Adv. plentifullyn. plenty ~  Adj. more than sufficient ~  Syn. abundant\n وافر، فراوان\n\n-Examples of Miros art plentiful\n');
INSERT INTO `papers` VALUES ('2711', 'V. react ~  Adv. reactivelyn. a reply; a change that occurs when substances are mixed ~  Adj. reactive, reactionary ~  Syn. response\n واکنش، انفعال، عکس العمل، انعکاس، واکنشى\n\n-When chlorine and ammonia are mixed, The chemical reaction causes chlorine gas');
INSERT INTO `papers` VALUES ('2712', ' Adv. rhythmicallyn. a regular pattern, usually in music ~  Adj. rhythmic ~  Syn. pulse\n وزن، سجع، ميزان، آهنگ موزون، نواخت\n\n-The rhythm of The rain hitting The roof put him to sleep.\n');
INSERT INTO `papers` VALUES ('2713', ' Adv. scenicallyn. scenery, scene ~  Adj. concerning pleasant natural surroundings ~  Syn. picturesque\n صحنه اى، نمايشى، مجسم کننده، خوش منظر\n\n-The scenic route to The summit is much more interesting than The fastest route.\n');
INSERT INTO `papers` VALUES ('2714', ' Adv. shallowlyn. shallowness ~  Adj. not far from top to bottom ~  Syn. superficial\n کم ژرفا، کم آب، سطحى، کم عمق کردن\n\n-Estuaries are typically shallow bodies of water.\n');
INSERT INTO `papers` VALUES ('2715', 'V. shelter n. shelter ~  Adj. protected from harmful elements; isolated from reality ~  Syn. shelter\n پناهنده شدن، پناه گرفتن، سرپناه، حفاظ، پناه بردن، پناه دادن، پناهگاه، حمايت، محافظت کردن، پناه دادن\n\n-She has led a sheltered life since Her parents have');
INSERT INTO `papers` VALUES ('2716', 'V. vanish  ~  Adj. going out of sight ~  Syn. disappearing\n ناپديد شدن، غيب شدن\n\n-The red squirrel is a vanishing species that needs a protected habitat to survive.\n');
INSERT INTO `papers` VALUES ('2717', 'V. account n. accounting, accountant, a report of an event; money kept in a bank; a statement of something used or received, usually a financial reportaccount ~  Adj. accountable ~  Syn. story\n شمردن، حساب کردن، محاسبه نمودن، صورت حساب، گزارش، بيان علت \n\n');
INSERT INTO `papers` VALUES ('2718', '  ~  Adj. very old, old-fashioned; no longer used ~  Syn. ancient\n کهنه، قديمى، غير مصطلح( بواسطه قدمت)\n\n-These archaic methods of farming must be brought up to date.\n');
INSERT INTO `papers` VALUES ('2719', 'V. to cause to go faster; move forward more quickly ~  Adv. hastilyn. hastiness ~  Adj. hasty ~  Syn. accelerate\n شتاباندن، شتافتن\n\n-After notifying His family of The accident, he hastened to add that he had not been hurt.\n');
INSERT INTO `papers` VALUES ('2720', ' n. color  ~  Syn. color\n رنگمايه، رنگ، شکل، تصوير، ظاهر، نما، صورت، منظر\n\n-The hue of The sunset was beautiful.\n');
INSERT INTO `papers` VALUES ('2721', 'V. illustrate n. illustrator, a visual image, typically used to explain ~  Adj. illustrated, illustrative ~  Syn. picture\n تشريح، عکس، شرح، توضيح، مثال، تصوير\n\n-The illustration makes The process of condensation more understandable.\n');
INSERT INTO `papers` VALUES ('2722', ' n. inaction, inactivity ~  Adj. not moving; not involved ~  Syn. idle\n غيرفعال، بى کاره، سست، بى حال، بى اثر، خنثى، کساد\n\n-The virus remains inactive for a long period of time.\n');
INSERT INTO `papers` VALUES ('2723', ' Adv. intricatelyn. intricacy ~  Adj. having many parts; finely detailed ~  Syn. complex\n بغرنج، پيچيده\n\n-The intricate design of The vase made It a valuable piece for Her collection.\n');
INSERT INTO `papers` VALUES ('2724', 'V. magnify ~  Adv. magnificentlyn. magnification, of great size or importance to increase ~  Adj. magnificant ~  Syn. dimension\n دامنه، حيطه عمل، کشش، تعداد، بزرگى، اهميت، شکوه، اندازه، مقدار\n\n-The magnitude of shock waves determines The damage that occur');
INSERT INTO `papers` VALUES ('2725', 'V. to have to do something ~  Adv. obliginglyn. obligation ~  Adj. obliging, obligatory ~  Syn. require\n مجبور کردن، وادار کردن، متعهد شدن، لطف کردن\n\n-She obliged he friend to choose a new lab partner.\n');
INSERT INTO `papers` VALUES ('2726', 'V. to ignore or neglect  ~  Adj. overlooked ~  Syn. disregard\n مسلط يا مشرف بودن بر، چشم پوشى کردن، چشم انداز\n\n-Scientists must not overlook any aspect of experimental procedure.\n');
INSERT INTO `papers` VALUES ('2727', ' n. polling, pollster, questionnaire; a vote of public opinion  ~  Syn. survey\n رأى شمارى، حوزه رأى گيرى، رأى دادن، نمونه برداشتن، سر شمارى کردن\n\n-The poll indicated that Conservation of The environment was The number one issue With college students.\n');
INSERT INTO `papers` VALUES ('2728', ' n. place where something can be found; job  ~  Syn. location\n شغل رسمى، موقعيت، وضعيت، موضع، مرتبه، جايگاه، قراردادن ياگرفتن\n\n-he held a very high position In The firm.\n');
INSERT INTO `papers` VALUES ('2729', ' n. practicality ~  Adj. convenient or effective ~  Syn. functional\n قابل استفاده، سودمند، آزموده، کابردى، عملى، اهل عمل\n\n-Her ambitious plan was not very practical\n');
INSERT INTO `papers` VALUES ('2730', 'V. predominate ~  Adv. predominantlyn. predominance ~  Adj. the most noticeable or powerful element ~  Syn. principal\n غالب، مسلط، حکمفرما، نافذ، عمده، برجسته\n\n-The predominant export of The Middle East is petroleum.\n');
INSERT INTO `papers` VALUES ('2731', 'V. to cause something to happen; to do quickly; to be on time ~  Adv. promptlyn. promptness  ~  Syn. induce\n اعلان، فورى، بيدرنگ، سريع کردن، به فعاليت واداشتن، برانگيختن، سريع، عاجل، چالاک\n\n-His emotional plea prompted The director to give him a second ch');
INSERT INTO `papers` VALUES ('2732', ' n. analogy ~  Adj. alike in some way ~  Syn. similar to\n مانند، قابل مقايسه، قابل قياس، مشابه\n\n-The action of light waves is analogous to The action of sound waves.\n');
INSERT INTO `papers` VALUES ('2733', 'V. approximate ~  Adv. almost correct; not exactn. approximation ~  Adj. approximate ~  Syn. around\n تقريباً\n\n-There are approximately 100 billion galaxies in the universe.\n');
INSERT INTO `papers` VALUES ('2734', 'V. to make something happen by necessity or force ~  Adv. compellingly ~  Adj. compelling ~  Syn. obliged\n مجبورکردن، وادار کردن\n\n-The representatives were compelled to vote In favor of The legislation despite Their personal opposition to it.\n');
INSERT INTO `papers` VALUES ('2735', ' Adv. formidably ~  Adj. difficult; causing worry or fear ~  Syn. overwhelming\n ترسناک، سخت، دشوار، نيرومند، قوى، سهمگين\n\n-Their formidable opponents gave No sign of weakness.\n');
INSERT INTO `papers` VALUES ('2736', 'V. intrude n. intrusion, intruder ~  Adj. intrusively, the state of being inside or upon something when not desired to be there by others ~  Syn. annoying\n فضول، سرزده، ناخوانده، به زورداخل شونده، فرو رونده\n\n-The intrusive Bacteria caused His condition to');
INSERT INTO `papers` VALUES ('2737', ' Adv. happening repeatedly, occurring at regular intervals Something published at regular intervalsn. periodical ~  Adj. periodic, periodical ~  Syn. sometimes\n به صورت دوره اى، در فواصل معين\n\n-Some materials periodically demonstrate unusual behavior When');
INSERT INTO `papers` VALUES ('2738', '  ~  Adj. likely to do something ~  Syn. inclined to\n حالت درازکش، در معرض، متمايل، مستعد، مهيا، درازکش، دمر\n\n-most liquids are prone to contract When frozen.\n');
INSERT INTO `papers` VALUES ('2739', 'V. prophesy ~  Adv. propheticallyn. prophecy, prophet ~  Adj. correctly telling about future events ~  Syn. predictive\n مبنى بر پيشگويى، پيشگويانه\n\n-His prophetic power were investigated by a team of psychologists.\n');
INSERT INTO `papers` VALUES ('2740', ' Adv. proportionally, proportionatelyn. relationship of size or importance when compared to another object or person. ~  Adj. proportional, proportionate ~  Syn. dimensions\n اندازه گرفتن، حجم، اندازه، اهميت \n\n-The goal of establishing a space station Will');
INSERT INTO `papers` VALUES ('2741', 'V. ready ~  Adv. willingly; easilyn. readiness ~  Adj. ready ~  Syn. freely\n با ميل، به آسانى، به سهولت\n\n-The workers readily complained about The food In The cafeteria.\n');
INSERT INTO `papers` VALUES ('2742', 'V. rely ~  Adv. in a trusted wayn. reliability, reliance ~  Adj. reliable, reliant ~  Syn. dependably\n به طور قابل اعتماد\n\n-an appliance must perform its task reliably to be popular With consumers.\n');
INSERT INTO `papers` VALUES ('2743', ' Adv. unwillinglyn. reluctance ~  Adj. reluctant ~  Syn. hesitatingly\n با اکراه، از روى تأمل، با دودلى\n\n-Although not completely satisfied With The contract, The officials reluctantly agreed to sign it.\n');
INSERT INTO `papers` VALUES ('2744', ' n. fame ~  Adj. renowned ~  Syn. prominence\n آوازه، نام، شهرت، معروفيت، مشهور کردن\n\n-This school is of great renown\n');
INSERT INTO `papers` VALUES ('2745', 'V. to give up something of value for the common good n. sacrifice ~  Adj. sacrificial ~  Syn. concession\n از دست دادن، قربانى براى شفاعت، قربانى دادن، فداکارى کردن\n\n-he sacrificed His day off to help clean up The neighborhood.\n');
INSERT INTO `papers` VALUES ('2746', 'V. triumph ~  Adv. triumphantlyn. a victory; a success ~  Adj. triumphant, triumphal ~  Syn. achievement\n پيروزى، پيروزمندانه، فتح وظفر، پيروزشدن\n\n-His career was characterized by one triumph After another.\n');
INSERT INTO `papers` VALUES ('2747', 'V. afford ~  Adv. affordably ~  Adj. able to be done, usually refers to something you can do without damage orloss ~  Syn. economical\n حاصل کردن، تهيه کردن، موجب شدن، از عهده برآمدن، استطاعت داشتن\n\n-The new dictionary is quite affordable\n');
INSERT INTO `papers` VALUES ('2748', 'V. contaminate n. contamination ~  Adj. to make something impure by adding something dirty or a poisonous substance ~  Syn. pollute\n آلوده کردن، سرايت دادن\n\n-This contaminated water supply must be closed to The public.\n');
INSERT INTO `papers` VALUES ('2749', 'V. discern ~  Adv. discerniblyn. discernment ~  Adj. noticeable; easily seen ~  Syn. detectable\n قابل تميز، قابل تشخيص\n\n-a feeling of anxiety was discernible among The members of The team.\n');
INSERT INTO `papers` VALUES ('2750', 'V. flourish  ~  Adj. active and growing; healthy ~  Syn. thriving\n آباد، ترقى کننده\n\n-Small flourishing companies would be harmed by an increase In The minimum wage.\n');
INSERT INTO `papers` VALUES ('2751', 'V. to support; to keep in good condition n. maintenance  ~  Syn. preserve\n پشتيبانى کردن، نگهدارى کردن، ادامه دادن، حمايت کردن از، مدعى بودن، نگه داشتن، برقرار داشتن\n\n-The building had to be renovated Because It was not well maintained\n');
INSERT INTO `papers` VALUES ('2752', ' n. mediocrity ~  Adj. of average quality; not good or bad ~  Syn. average\n حد وسط، متوسط، ميان حال، وسط\n\n-This is a mediocre research report.\n');
INSERT INTO `papers` VALUES ('2753', ' Adv. negligiblyn. neglect, negligence ~  Adj. neglected, neglectful, negligent, hardly noticeable; scarcely detectable to ignore; to give little attention ~  Syn. insignificant\n ناچيز، جزئى، بى اهميت، قابل فراموشى\n\n-The presence of The first person singu');
INSERT INTO `papers` VALUES ('2754', 'V. parallel n. parallel ~  Adj. being almost of the same type or time; comparable ~  Syn. similar\n همسو، نظير، مطابق بودن با، برابر بودن، انشعاب، همگام، متوازى، موازى کردن، برابر کردن، همزمان\n\n-We have lead parallel lives.\n');
INSERT INTO `papers` VALUES ('2755', ' Adv. peculiarlyn. peculiarity ~  Adj. unusual; uncommon ~  Syn. strange\n عجيب وغريب، ويژه\n\n-This plastic has a peculiar texture.\n');
INSERT INTO `papers` VALUES ('2756', '  ~  Adj. very strong ~  Syn. powerful\n قوى، پرزور، نيرومند\n\n-he gave a potent speech at The convention.\n');
INSERT INTO `papers` VALUES ('2757', ' Adv. remarkably ~  Adj. worthy of mention; uncommon ~  Syn. exceptional\n قابل توجه، عالى، جالب توجه\n\n-The invention of The radio was a remarkable achievement.\n');
INSERT INTO `papers` VALUES ('2758', 'V. scatter  ~  Adj. to be spread out or separated widely ~  Syn. dispersed\n پراکنده، تار و مار\n\n-Hurricane Andrew left debris scattered throughout Miami.\n');
INSERT INTO `papers` VALUES ('2759', 'V. solidify ~  Adv. solidlyn. solidification ~  Adj. having good quality, being well made; firm; not a gas or liquid ~  Syn. steady\n ثابت، يکپارچه، يکسان، ماده جامد، سفت، محکم، قوى، خالص، منجمد، يک پارچه، مکعب، سه بعدى، توپر، نيرومند، قابل اطمينان\n\n-The l');
INSERT INTO `papers` VALUES ('2760', '  ~  Adj. a little ~  Syn. slight\n قدرى، مقدار نامعلومى، تاحدى، مختصرى\n\n-They feel somewhat tired After The mile run.\n');
INSERT INTO `papers` VALUES ('2761', ' Adv. tediouslyn. tedium ~  Adj. long and tiring ~  Syn. monotonous\n ملالت آور، خسته کننده، کسل کننده، کج خلق، ناراضى\n\n-The tedious lecture bored most of The audience.\n');
INSERT INTO `papers` VALUES ('2762', ' Adv. short, usually in timen. brevity ~  Adj. brief ~  Syn. concisely\n به طور خلاصه\n\n-The visiting Professor spoke briefly at The faculty meeting.\n');
INSERT INTO `papers` VALUES ('2763', 'V. to cause to move along a fixed path; to move freely n. circulation ~  Adj. circulatory ~  Syn. move freely\n داير کردن، منتشر کردن، انتشار دادن، رواج دادن، بخشنامه کردن، گردش کردن، به گردش درآوردن، منتشر شدن\n\n-The news of the presidents visit circulate');
INSERT INTO `papers` VALUES ('2764', 'V. consist ~  Adv. without changing; keeping the same principles, ideas, or qualityn. consistency ~  Adj. consistent ~  Syn. dependably\n به طور موافق، بدون تناقض\n\n-The temperature must be maintained consistently at 75° centigrade.\n');
INSERT INTO `papers` VALUES ('2765', 'V. to show or demonstrate n. exhibit, exhibition, exhibitor  ~  Syn. display\n نمايش دادن، درمعرض نمايش قراردادن، ارائه دادن، ابراز کردن\n\n-The compound exhibits The qualities of an acid.\n');
INSERT INTO `papers` VALUES ('2766', 'V. to establish, start up a philanthropic organization n. foundation, founder  ~  Syn. establish\n برپاکردن، بنياد نهادن، تأسيس کردن، ريختن، قالب کردن \n\n-The wealthy woman founded a hospital In Her home town.\n');
INSERT INTO `papers` VALUES ('2767', ' Adv. not following established rules; not desirablen. impropriety ~  Adj. improper ~  Syn. inappropriately\n بطورناصحيح، بطورغلط، بطور نامناسب\n\n-The disappointing outcome was a result of an improperly prepared petri dish.\n');
INSERT INTO `papers` VALUES ('2768', ' Adv. acting without thinkingn. impulse, impulsiveness ~  Adj. impulsive ~  Syn. capriciously\n با نيروى آنى و بدون اراده، از روى تحريک\n\n-She reacted impulsively to The loud noise.\n');
INSERT INTO `papers` VALUES ('2769', ' n. infrequency ~  Adj. infrequent, almost never ~  Syn. rarely\n کم، کمتر، به ندرت، گاه گاهى\n\n-Tornadoes occur infrequently In The eastern part of The United States.\n');
INSERT INTO `papers` VALUES ('2770', 'V. isolate n. isolation ~  Adj. kept separated from others ~  Syn. secluded\n منزوى\n\n-The failure of The communications system left The towns isolated\n');
INSERT INTO `papers` VALUES ('2771', ' Adv. in a way clearly seen; not done secretly ~  Adj. overt ~  Syn. openly\n آشکارا\n\n-he overtly disregarded The regulations.\n');
INSERT INTO `papers` VALUES ('2772', ' Adv. in a deep way; showing deep knowledge of a subjectn. profundity ~  Adj. profound ~  Syn. significantly\n به طورعميق، زيادى\n\n-Everyone was profoundly impressed by The news reports.\n');
INSERT INTO `papers` VALUES ('2773', 'V. sharpen ~  Adv. showing sensitivity or quick thinking; showing a quick change in directionn. sharpness ~  Adj. sharp ~  Syn. quickly\n تند، سخت، به زيرکى، با صراحت\n\n-Car prices rose sharply over The past year.\n');
INSERT INTO `papers` VALUES ('2774', 'V. situate n. situation ~  Adj. being found in a certain place a current condition ~  Syn. located\n واقع شده در، واقع در، جايگزين\n\n-The resort town of Cancun is situated In The northern part of The Yucatan Peninsula.\n');
INSERT INTO `papers` VALUES ('2775', ' Adv. following; coming after something ~  Adj. subsequent ~  Syn. afterwards\n سپس، متعاقباً\n\n-The public applauded the presidents actions, and subsequently his ratings in the polls improved.\n');
INSERT INTO `papers` VALUES ('2776', '  ~  Adj. unmistakably, clearly able to be determined ~  Syn. indisputable\n خالى از اشتباه و سوء تفاهم، بى ترديد\n\n-The markings of The insect provided an unmistakable identification of The species.\n');
INSERT INTO `papers` VALUES ('2777', ' n. chaos ~  Adj. being in complete disorder and confusion ~  Syn. disorganized\n پرهرج ومرج، بى نظم\n\n-The traffic In Seoul is often chaotic\n');
INSERT INTO `papers` VALUES ('2778', 'V. characterize ~  Adv. characteristicallyn. characteristic, characterization ~  Adj. an easily recognized quality of something ~  Syn. typical\n صفت اختصاصى يا مميز، نشان ويژه علامت مشخصه، نهادين، نشان ويژه، صفت مميزه، مشخصات، مشخصه\n\n-The markings on that');
INSERT INTO `papers` VALUES ('2779', 'V. controversially n. controversy ~  Adj. something that causes disagreement or argument. ~  Syn. debatable\n مباحثه اى، جدلى، جدال آميز، هم ستيزگرانه\n\n-The governor made a controversial decision to raise taxes.\n');
INSERT INTO `papers` VALUES ('2780', 'V. to give an example  ~  Adj. exemplary ~  Syn. symbolize\n بامثال فهمانيدن، بانمونه نشان دادن\n\n-The recent downturn In The housing industry exemplifies The poor economic conditions.\n');
INSERT INTO `papers` VALUES ('2781', 'V. gratify ~  Adv. gratefullyn. gratification, gratefulness ~  Adj. grateful, giving pleasure or a feeling of accomplishment; showing thanks ~  Syn. satisfying\n موجب خوشنودى، لذت بخش\n\n-Studying abroad can be a very gratifying experience.\n');
INSERT INTO `papers` VALUES ('2782', 'V. interpreting, to understand the meaning of something n. interpreter, interpretation  ~  Syn. clarify\n تفسير کردن، ترجمه کردن، ترجمه شفاهى کردن\n\n-The ambiguous speech was very difficult to interpret\n');
INSERT INTO `papers` VALUES ('2783', 'V. to cause something to begin n. launch, launching  ~  Syn. initiate\n راه انداختن، شروع کردن کار، اجراى حمله، شروع عمليات، روانه کردن، مامور کردن، شروع کردن، اقدام کردن\n\n-The company launched a new program to attract more clients.\n');
INSERT INTO `papers` VALUES ('2784', ' Adv. legitimatelyn. legitimacy ~  Adj. reasonable, lawful ~  Syn. authentic\n عذر قانونى، حلال زاده، برحق، قانونى، مشروع\n\n-The engineer had a legitimate reason for changing The design of The building.\n');
INSERT INTO `papers` VALUES ('2785', ' Adv. particularly ~  Adj. a certain way or thing; unusual; hard to please especially ~  Syn. specific\n مخصوص، ويژه، خاص، دقيق، نکته بين، خصوصيات، منحصر بفرد، سختگير\n\n-The speaker has a particular way of persuading His audience.\n');
INSERT INTO `papers` VALUES ('2786', ' Adv. radiantlyn. radiance, radiation, radiator ~  Adj. sending out in all directions, especially heat or light ~  Syn. bright\n تابان، گسترده، شعاع گستر، پرجلا، درخشنده، شعاعى، ساطع\n\n-The actors radiant smile captivated the audience.\n');
INSERT INTO `papers` VALUES ('2787', ' n. the top of a mountain range; a raised part of any surface  ~  Syn. crest\n خاک پشته، کاکل، رشته تپه، تپه ماهور، برآمدگى، لبه، خرپشته، نوک، مرز بندى کردن، شيار دار کردن\n\n-a bald eagle stood on The ridge of The tall office building.\n');
INSERT INTO `papers` VALUES ('2788', 'V. the length of time or distance from one limit to the other; to cross n. span  ~  Syn. cover\n پل زدن روى رودخانه، چشمه، افسار، محدوده، گستردگى، پوشش، ظرفيت، يک وجب، فاصله معين، وجب کردن، اندازه گرفتن، پل بستن\n\n-The old mans life spanned two centuries.\n');
INSERT INTO `papers` VALUES ('2789', ' Adv. spontaneouslyn. spontaneity ~  Adj. unplanned; uncontrolled ~  Syn. instinctive\n خود به خودى، خود انگيز، بى اختيار، فورى\n\n-The spontaneous combustion inside The cylinder creates The power of The motor.\n');
INSERT INTO `papers` VALUES ('2790', 'V. stream n. a natural flow of something; a pouring out  ~  Syn. river\n روانه، جريان آب آبيارى، جويبار، جوب، رودخانه، مسيل ،جريان، نهر\n\n-There was a constant stream of information coming From The White house.\n');
INSERT INTO `papers` VALUES ('2791', ' Adv. strikingly ~  Adj. drawing special attention to ~  Syn. remarkable\n برجسته، قابل توجه، مؤثر، گيرنده، زننده\n\n-His striking proposal saved The company From b6ankruptcy.\n');
INSERT INTO `papers` VALUES ('2792', ' Adv. having a tendency to do something; likelyn. aptness ~  Adj. apt ~  Syn. appropriately\n ماهرانه، از روى لياقت\n\n-It was an aptly timed remark.\n');
INSERT INTO `papers` VALUES ('2793', 'V. demonstrate ~  Adv. demonstrablyn. a show or exhibit overtly showing emotion ~  Adj. demonstrative, demonstrable ~  Syn. display\n برهان، نمايش تجربى، تظاهرات، نمايش، اثبات\n\n-The demonstration clarified The procedure for everyone.\n');
INSERT INTO `papers` VALUES ('2794', ' n. things combined to make something; the contents of something  ~  Syn. elements\n جزء ترکيبى، ذرات، داخل شونده، عوامل، عناصر\n\n-The ingredients of The product are kept secret.\n');
INSERT INTO `papers` VALUES ('2795', ' Adv. in an unthinking manner; not chosen ~  Adj. involuntary ~  Syn. automatically\n بى اختيار، بدون اراده، بطورغيرارادى يا غيرعمدى\n\n-he involuntarily agreed to work overtime.\n');
INSERT INTO `papers` VALUES ('2796', ' Adv. marvelouslyn. something that surprises or impresses ~  Adj. marvelous ~  Syn. wonder\n چيز شگفت، شگفتى، تعجب، اعجاز، حيرت زده شدن، شگفت داشتن\n\n-The Great Wall of China is one of the worlds marvels\n');
INSERT INTO `papers` VALUES ('2797', 'V. measure ~  Adv. measurablyn. measurement ~  Adj. able to determine how much or how many ~  Syn. assessable\n قابل اندازه گيرى\n\n-Some personal characteristics, such as Good teaching, are hardly measurable\n');
INSERT INTO `papers` VALUES ('2798', 'V. moderate ~  Adv. moderatelyn. moderation ~  Adj. not too much, not too little to reduce ~  Syn. medium\n متوسط، معتدل، ملايم، ميانه رو، مناسب، تعديل کردن\n\n-She made The best of Her moderate dancing ability.\n');
INSERT INTO `papers` VALUES ('2799', ' Adv. oddlyn. oddity ~  Adj. unusual ~  Syn. strange\n غريب، آدم عجيب \n\n-It is odd to find a person who speaks Many languages.\n');
INSERT INTO `papers` VALUES ('2800', 'V. reflect n. a picture or element cast back ~  Adj. reflected ~  Syn. image\n انعکاس، بازتاب، انديشه، تفکر، پژواک\n\n-His bright smile was a reflection of His satisfaction.\n');
INSERT INTO `papers` VALUES ('2801', 'V. suppose ~  Adv. according to reports or hearsay; widely believed or acceptedn. supposition ~  Adj. supposed ~  Syn. presumably\n بطور فرضى\n\n-The new trains can supposedly reach speeds of 150 miles per hour.\n');
INSERT INTO `papers` VALUES ('2802', 'V. sustain  ~  Adj. sustenance, continuing in a constant way; remaining strong ~  Syn. consistent\n ثابت، تند، پايدار نگهداشتن، متحمل شدن، تقويت کردن، حمايت کردن از\n\n-Sustained rainfall is The Only hope They have for relief From The drought.\n');
INSERT INTO `papers` VALUES ('2803', 'V. symbolize ~  Adv. symbolicallyn. a sign or object that represents something or somebody marks ~  Adj. symbolic ~  Syn. marks\n علائم\n\n-The strange symbols found In Egyptian tombs have intrigued historians for centuries.\n');
INSERT INTO `papers` VALUES ('2804', 'V. synthesize ~  Adv. syntheticallyn. the mixing of separate things to form a whole not made by nature ~  Adj. synthetic ~  Syn. combination\n ساخت، نتيجه گيرى، استنتاج، ترکيب، تلفيق \n\n-The language of Papiamento is a synthesis of Dutch and native Indian l');
INSERT INTO `papers` VALUES ('2805', ' Adv. tangiblyn. tangibility ~  Adj. real; that which can be felt ~  Syn. concrete\n قابل لمس، محسوس، لمس کردنى\n\n-The work of a teacher seldom produces tangible results Until years After a student has graduated.\n');
INSERT INTO `papers` VALUES ('2806', 'V. tighten ~  Adv. being fixed in place; close; leaving no freedomn. tightness ~  Adj. tight ~  Syn. firmly\n سفت، محکم، تنگ\n\n-The shirt fits too tightly\n');
INSERT INTO `papers` VALUES ('2807', 'V. aggravate n. aggravation ~  Adj. making worse; annoying ~  Syn. irritating\n سخت کننده، شديد کننده\n\n-The aggravating delay was caused by road repairs.\n');
INSERT INTO `papers` VALUES ('2808', 'V. amuse ~  Adv. amusinglyn. something that holds interest and is enjoyable ~  Adj. amusing ~  Syn. diversion\n سرگرمى، تفريح، گيجى، گمراهى، فريب خوردگى، پذيرايى، نمايش\n\n-We listened in amusement as he tried to convince his friend to lend him $50.\n');
INSERT INTO `papers` VALUES ('2809', 'V. conceive ~  Adv. feasibly; believable ~  Adj. conceivable ~  Syn. possibly\n بطور امکان پذير\n\n-They could conceivably earn first place With Their science project.\n');
INSERT INTO `papers` VALUES ('2810', 'V. to change from one form or state to another n. conversion ~  Adj. convertible ~  Syn. alter\n مقلوب کردن، امتياز گرفتن، تبديل کردن، برگرداندن، وارونه کردن، معکوس کردن \n\n-When boiled, liquids convert to gases.\n');
INSERT INTO `papers` VALUES ('2811', ' n. cure ~  Adj. being able to restore to good condition ~  Syn. healing.\n داراى خاصيت درمانى، علاج بخش، شفا بخش\n\n-The curative properties of certain plants have been well documented.\n');
INSERT INTO `papers` VALUES ('2812', 'V. debilitate n. debility ~  Adj. weakening ~  Syn. weakening\n ناتوان سازى، تضعيف\n\n-The lack of investment savings has a debilitating effect on The economy.\n');
INSERT INTO `papers` VALUES ('2813', 'V. to use up; to reduce greatly n. depletion ~  Adj. depleted ~  Syn. consume\n تهى کردن، خالى کردن، به ته رسانيدن\n\n-She depleted all of Her savings to buy The word processor.\n');
INSERT INTO `papers` VALUES ('2814', '  ~  Adj. of a certain amount; having an end; not infinite ~  Syn. limited\n متناهى، محدود\n\n-There were a finite number of explanations for The unusual reactions.\n');
INSERT INTO `papers` VALUES ('2815', 'V. to sense; to become aware of ~  Adv. perceptibly, perceptivelyn. perception ~  Adj. perceivable, perceptive ~  Syn. observe\n درک کردن، دريافتن، مشاهده کردن، ديدن، ملاحظه کردن\n\n-We perceive major differences between The two political parties.\n');
INSERT INTO `papers` VALUES ('2816', 'V. secure ~  Adv. securelyn. the feeling of freedom from danger, doubt, or worry ~  Adj. secure ~  Syn. safety\n حفاظت، ايمنى، امنيت، آسايش خاطر، اطمينان، مصونيت، تضمين، ضامن\n\n-Her sense of security increased as Her grades improved.\n');
INSERT INTO `papers` VALUES ('2817', ' n. toxicity ~  Adj. harmful; capable of being fatal ~  Syn. poisonous\n مسموم، زهرآلود، سمى، زهراگين\n\n-Disposal of toxic wastes is an ongoing problem.\n');
INSERT INTO `papers` VALUES ('2818', 'V. tranquilize ~  Adv. tranquillyn. calm; quietness ~  Adj. tranquil ~  Syn. peacefulness\n آرامش، آسودگى، آسايش خاطر، راحت\n\n-The tranquility of The Lake at sunrise inspired a profound sense of well-being.\n');
INSERT INTO `papers` VALUES ('2819', 'V. to catch and hold onto, usually by trickery; deceived n. trap ~  Adj. trapped ~  Syn. retain\n سد کردن، به دام انداختن، در تله انداختن \n\n-I was trapped into paying for The meal.\n');
INSERT INTO `papers` VALUES ('2820', ' Adv. clearly true ~  Adj. undeniable ~  Syn. absolutely\n انکار ناپذير\n\n-of all The planets In our solar system, Earth is undeniably The most conducive to supporting life.\n');
INSERT INTO `papers` VALUES ('2821', 'V. underestimate  ~  Adj. guessed lower than the actual quality or quantity ~  Syn. miscalculated\n اشتباه محاسبه کردن، اشتباه حساب کردن، پيش بينى غلط کردن\n\n-The underestimated demand for tickets made The Theater manager plan better for The next performanc');
INSERT INTO `papers` VALUES ('2822', 'V. to know, remember, and accept the existence of something n. acknowledgment ~  Adj. acknowledged ~  Syn. recognize\n تأييد کردن، قدردانى کردن، اعتراف کردن، تصديق کردن\n\n-The foreman acknowledged The fact that There had been a mistake In The design of The ');
INSERT INTO `papers` VALUES ('2823', 'V. to gain or come to possess n. acquisition ~  Adj. acquisitive ~  Syn. obtain\n به دست آوردن، تعيين، گرفتن، حاصل کردن، اندوختن، پيداکردن\n\n-he acquired two beautiful paintings during His visit to Taipei.\n');
INSERT INTO `papers` VALUES ('2824', 'V. to become a part of n. assimilation  ~  Syn. incorporate\n يکسان کردن، هم جنس کردن، شبيه ساختن، تحليل رفتن، سازش کردن، تلفيق کردن، همانند ساختن\n\n-The United States of America has assimilated people From all parts of The world.\n');
INSERT INTO `papers` VALUES ('2825', 'V. a variety  ~  Adj. assorted ~  Syn. selection\n ترتيب، مجموعه، دسته بندى، طبقه بندى\n\n-you have an assortment of elective courses From which to choose.\n');
INSERT INTO `papers` VALUES ('2826', ' n. the standard of; the degree of goodness  ~  Syn. quality\n قطرگلوله، قطردهانه تفنگ يا توپ، کاليبر، گنجايش، استعداد، وضعيت، کيفيت \n\n-The high caliber of Her work earned Her a raise In pay.\n');
INSERT INTO `papers` VALUES ('2827', 'V. condense  ~  Adj. made smaller; shortened; merged ~  Syn. summarized\n فشرده، خلاصه شده، تغليظ شده، چگاليده\n\n-This is a condensed version of The original research report.\n');
INSERT INTO `papers` VALUES ('2828', 'V. contradict ~  Adv. not agreeing with the facts or previous&nbsp;&nbsp;statements made on the subject; declared wrongn. contradiction  ~  Syn. inconsistent\n متضاد، متناقض، مخالف، متباين، ضد ونقيض، مغاير\n\n-It is contradictory to say that you know French ');
INSERT INTO `papers` VALUES ('2829', 'V. to pay no attention n. disregard  ~  Syn. ignore\n ناديده گرفتن، اعتنا نکردن، عدم رعايت\n\n-They disregarded The No parking signs and were ticketed by The police.\n');
INSERT INTO `papers` VALUES ('2830', '  ~  Adj. having much monetary or sentimental value; beautiful ~  Syn. cherished\n قيمتى، فوق العاده، گرانبها، نفيس، پرارزش\n\n-This golden ring is my most precious possession.\n');
INSERT INTO `papers` VALUES ('2831', ' Adv. prominentlyn. prominence ~  Adj. famous; having a high position ~  Syn. renowned\n حساس، برجسته، والا\n\n-Their talent for locating oil deposits made them prominent geologists In The corporation.\n');
INSERT INTO `papers` VALUES ('2832', 'V. require, requisition n. requirement, requisition ~  Adj. needed for a specific purpose a formal request ~  Syn. demanded\n شرط لازم، لازمه، احتياج، چيز ضرورى\n\n-Here is the list of requisite courses for the masters degree in biology.\n');
INSERT INTO `papers` VALUES ('2833', 'V. to organize; to make clear n. unraveling  ~  Syn. separate\n از هم باز کردن، از گير در آوردن، حل کردن\n\n-The detective was not able to unravel The mystery of The missing money.\n');
INSERT INTO `papers` VALUES ('2834', ' Adv. vaguelyn. vagueness ~  Adj. not clear; ambiguous ~  Syn. unclear\n مبهم، غير معلوم، سر بسته وابهام دار\n\n-She Only has vague memories of Her childhood.\n');
INSERT INTO `papers` VALUES ('2835', ' Adv. vastly ~  Adj. very much; very large ~  Syn. huge\n پهناور، وسيع، بزرگ، زياد، عظيم، بيکران\n\n-I have noticed a vast improvement In your English vocabulary.\n');
INSERT INTO `papers` VALUES ('2836', ' Adv. voluminouslyn. the amount of something contained in a space holding a lot ~  Adj. voluminous ~  Syn. quantity\n ظرفيت، مقدار، برحجم افزودن ،برزگ شدن( حجم)\n\n-The volume of information that a computer diskette can hold is astounding.\n');
INSERT INTO `papers` VALUES ('2837', ' n. a special quality that endears other people to the person who has this quality ~  Adj. charismatic ~  Syn. appeal\n عطيه الهى، جذبه روحانى، گيرايى\n\n-She has a charisma that No other candidate possesses.\n');
INSERT INTO `papers` VALUES ('2838', ' Adv. cleverlyn. cleverness ~  Adj. intelligent; resourceful ~  Syn. astute\n زرنگ، زيرک، باهوش، با استعداد، چابک\n\n-Everyone appreciated Their clever idea.\n');
INSERT INTO `papers` VALUES ('2839', 'V. to make someone see things your way ~  Adv. convincingly ~  Adj. convincing ~  Syn. persuade\n متقاعد کردن، قانع کردن\n\n-They could not convince The girls to go to The dance With them.\n');
INSERT INTO `papers` VALUES ('2840', 'V. to last; to suffer pain n. endurance ~  Adj. endurable, enduring ~  Syn. persevere\n تحمل کردن، بردبارى کردن دربرابر، طاقت چيزى را داشتن، تاب چيزى را آوردن\n\n-How he is able to endure Living next to The airport is beyond my comprehension.\n');
INSERT INTO `papers` VALUES ('2841', 'V. to give up; to have something taken away, usually by rule or regulation n. forfeit  ~  Syn. relinquish\n جريمه کردن، محروميت، خطا کردن، جريمه دادن، هدر کردن\n\n-Usually you must forfeit your native countrys citizenship to become a citizen of another coun');
INSERT INTO `papers` VALUES ('2842', ' Adv. precariously ~  Adj. not safe, firm, or steady ~  Syn. hazardous\n مشروط به شرايط معينى، مشکوک، التماس کننده، پرمخاطره\n\n-The diver put himself In a precarious situation among The sharks.\n');
INSERT INTO `papers` VALUES ('2843', 'V. severely n. severity ~  Adj. extreme; harmful ~  Syn. intense\n سختگيرانه، سخت گير، طاقت فرسا، شديد\n\n-The weather service issued a severe storm warning for most of Michigan.\n');
INSERT INTO `papers` VALUES ('2844', ' Adv. sporadically ~  Adj. not consistent; irregular ~  Syn. erratic\n تک وتوک، تک تک، پراکنده، انفرادى، گاه وبيگاه، گاه بگاه\n\n-The radio communications were subject to sporadic sunspot interference.\n');
INSERT INTO `papers` VALUES ('2845', ' n. superiority ~  Adj. excellent quality; above all the rest ~  Syn. exceptional\n عالى، رئيس، مافوق، ارشد، برتر، ممتاز\n\n-This is a superior fossil of a trilobite.\n');
INSERT INTO `papers` VALUES ('2846', ' Adv. wantonly ~  Adj. done without thought or consideration ~  Syn. senseless\n سرکش، حرف نشنو، بازيگوش، جسور، شرور شدن، گستاخ شدن، شهوترانى کردن، افراط کردن\n\n-Her wanton disregard of The rules was unexplainable.\n');
INSERT INTO `papers` VALUES ('2847', 'V. weaken ~  Adv. weaklyn. weakness ~  Adj. not strong; incapable ~  Syn. not strong; incapable\n سست، کم دوام، ضعيف، کم بنيه، کم زور، کم رو\n\n-The weak light was inadequate for reading.\n');
INSERT INTO `papers` VALUES ('2848', '  ~  Adj. found everywhere ~  Syn. extensive\n شايع، همه جا منتشر، گسترده\n\n-There is a widespread rumor that There Will be No class next Thursday.\n');
INSERT INTO `papers` VALUES ('2849', ' Adv. wiselyn. knowledge and understanding ~  Adj. wise ~  Syn. insight\n فرزانگى، خرد، حکمت، عقل، دانايى، دانش، معرفت\n\n-It is often said that wisdom is The product of experience.\n');
INSERT INTO `papers` VALUES ('2850', ' Adv. wittilyn. wit, wittiness, a joke; a funny story ~  Adj. witty ~  Syn. humor\n بذله گويى، شوخى، لطيفه گويى، مسخره\n\n-His witticisms captivated The audience.\n');
INSERT INTO `papers` VALUES ('2851', 'V. to make efforts to attain or gain something   ~  Syn. attract\n اظهار عشق کردن با، عشقبازى کردن با، خواستگارى کردن، جلب لطف کردن\n\n-The directors tried to woo The support of The union.\n');
